label("$x + \sqrt{3x + 6}$", (sqrt(3)/2, sqrt(3)/2));
label("$\sqrt{x + \sqrt{3x + 6}}$", (sqrt(3)/2, 0));
label("$\sqrt{x - \sqrt{3x + 6}}$", (sqrt(3)/2, sqrt(3)/2));
label("$6$", (sqrt(3)/2, sqrt(3)/2 + 0.5));
[/asy] najczęście
