unitsize(1 cm);

real f(real x) {
  return 2*x^4 + 3*x^3 - x^2 + 1;
}

draw(graph(f,-2,2),red);
draw((-2,-2)--(2,2));
draw((-2,0)--(2,0));
draw((0,-2)--(0,2));
draw((-0.5,-0.5)--(0.5,0.5));

label("$2x^4 + a_3 x^3 + a_2 x^2 + a_1 x + 1 = 0$", (0.5,0.5), NE);
[/asy]เสื้อผ้า
